jQuery(function(){$(".select2").select2({placeholder:"Choose one",searchInputPlaceholder:"Search",width:"100%"}),$(".select2-no-search").select2({minimumResultsForSearch:1/0,placeholder:"All categories",width:"100%"});function t(e){if(!e.id)return e.text;var l=$('<span><img src="https://laravelui.spruko.com/dashplex/build/assets/web-fonts/flag-icon-css/flags/4x3/'+e.element.value.toLowerCase()+'.svg" class="img-flag" /> '+e.text+"</span>");return l}$(".select2-flag-search").select2({templateResult:t,templateSelection:t,escapeMarkup:function(e){return e}}),$(".select2").on("click",()=>{document.querySelectorAll(".select2-search__field").forEach((l,c)=>{l.focus()})})});
